controllers.addActivityController.js:
import { addActivity } from '../services/activityService.js';

export const add = async (req, res) => {
    const content = req.body;
    try{
        const activity = await addActivity(content);
        res.status(201).json(activity);
    } catch(err) {
        res.status(500).json({message: err.message});
    }
}

controllers.deleteActivityController.js:
import { updateActivityById } from '../services/activityService.js';

export const remove = async (req, res) => {
    const activityId = req.params.id;
    const result = await updateActivityById(activityId, {status: 'deleted'});
    return !result ? res.status(500).json({message: `Error deleting ${activityId}`}) : res.status(200).json();
}

controllers.getActivityController.js:
import { getActivityById } from '../services/activityService.js';

export const get = async (req, res) => {
    const activityId = req.params.id;
    const activity = await getActivityById(activityId);
    if(activity) {
        res.status(200).json(activity);
    } 
    else {
        res.status(404).json({message: `error: activity ${activityId} not found`});
    }
}

controllers.updateActivityController.js:
import { updateActivityById } from '../services/activityService.js';

export const update = async (req, res) => {
    const activityId = req.params.id;
    const activity = await updateActivityById(activityId, req.body);
    if(activity) {
        res.status(200).json(activity);
    } 
    else {
        res.status(404).json({message: `error: activity ${activityId} not found`});
    }
}

