repository.ActivityRepository.js:
import activitySchema from "../schemas/activitySchema.js";

class ActivityRepository {
    async add(content) {
        const res = await activitySchema.create(content).catch((err) => {
            console.log(err);
        });
        return res.toObject();
    }

    async getById(id) {
        const res = await activitySchema.findById(id).catch((err) => {
            console.log(err);
        });
        return res.toObject();
    }

    async update(id, params) {
        console.log(id, params);
        const res = await activitySchema.findByIdAndUpdate(id, params, {new: true}).catch((err) => {
            console.log(err);
        });
        return res.toObject();
    }
}

export default new ActivityRepository();

routes.routes.js:
import express from 'express';

const router = express.Router();

import { activityBodyValidator, activityIdParamValidator } from '../validators/routeValidator.js';

import { add } from '../controllers/addActivityController.js';
import { get } from '../controllers/getActivityController.js';
import { update } from '../controllers/updateActivityController.js';
import { remove } from '../controllers/deleteActivityController.js';

router.post('/', activityBodyValidator, add);
router.get('/:id', activityIdParamValidator, get);
router.patch('/:id', activityIdParamValidator, activityBodyValidator, update);
router.delete('/:id', activityIdParamValidator, remove);

export const activityRoutes = router;

schemas.activitySchema.js:
import mongoose from "mongoose";

const activitySchemas = new mongoose.Schema(
    {
        name: String,
        description: String,
        status: { type: String, default: 'open'}
    },
    { 
        timestamps: true 
    }
);

export default mongoose.model('activity', activitySchemas);

server.js:
import express from 'express';
import { activityRoutes } from './routes/routes.js';
import { connect } from './database.js';

const host = 'localhost';
const port = 8001;
const app = express();

app.use(express.json());
connect().then(() => {
    app.use('/', activityRoutes);
    app.use((err, req, res, next) => {
        if(err?.error && err.error.isJoi) {
            res.status(400).json({type: err.type, message: err.error.toString()});
        }
        else{
            next(err);
        }
    })
    app.listen(port, host, () => {
        console.log(`Server avviato ${host}: ${port}.`)
    })
})

services.activityService.js:
import activityRepo from '../repository/ActivityRepository.js';

export const addActivity = async (content) => {
    return await activityRepo.add(content).catch((err) => {
        return null;
    });
}

export const getActivityById = async (id) => {
    return await activityRepo.getById(id).catch((err) => {
        return null;
    });
}

export const updateActivityById = async (id, params) => {
    return await activityRepo.update(id, params).catch((err) => {
        return null;
    })
}

